import './App.css';

class App extends Component {
  render(){
      return (
        <div className="App">
          <input type="text" placeholder="name" onchange={this.handleChange}/>
          <input type="number" placeholder="receipt id" onchange={this.handleChange}/>
          <input type="number" placeholder="price 1" onchange={this.handleChange}/>
          <input type="number" placeholder="price 2" onchange={this.handleChange}/>

          <button onClick={this.createAndDownloadPdf}>
            download P
          </button>
         </div>
      );
  }
}

export default App;
